const maxResults = 100;

const config = {
  jql: 'updated >= -120m',
  expand: "names,changelog",
  fields: [
    "key","summary","assignee","status","updated","project","issuetype",
    "priority","reporter","labels","created","duedate","resolution","components"
  ],
  maxResults,
};

// reset accumulator in static data
const data = $getWorkflowStaticData('global');
data.allIssues = [];

return [{ json: { ...config, nextPageToken: null } }];

const data = $getWorkflowStaticData('global');

const pageIssues = $json.issues || [];
if (!data.allIssues) data.allIssues = [];

// push is O(k), concat is O(n+k)
data.allIssues.push(...pageIssues);

const nextToken = $json.nextPageToken || null;
const init = $items("Code: Init")[0].json;

return [{
  json: {
    jql: init.jql,
    expand: init.expand,
    fields: init.fields,
    maxResults: init.maxResults,
    nextPageToken: nextToken
  }
}];

const data = $getWorkflowStaticData('global');
const allIssues = data.allIssues || [];

return [{
  json: {
    issues: allIssues,
    totalFetched: allIssues.length
  }
}];

const byCommentId = {};

for (const item of items) {
  const c = item.json;
  const commentId = c.CommentId;
  if (!commentId) continue;

  const text = c.SubContent?.text;

  // skip null/undefined
  if (text == null) continue;

  // optionally skip empty strings after trim
  if (typeof text === "string" && text.trim() === "") continue;

  if (!byCommentId[commentId]) {
    byCommentId[commentId] = { ...c, concatenatedText: "" };
  }

  byCommentId[commentId].concatenatedText +=
    (byCommentId[commentId].concatenatedText ? "" : "") + text;
}

// one output item per CommentId
return Object.values(byCommentId).map(v => ({ json: v }));

// items is available in n8n Code node when "Run Once for All Items" is selected

const grouped = {};

for (const item of items) {
  const issueObj = item.json.Issue || {};
  const key = issueObj.IssueKey;              // <-- your grouping key
  const fu  = issueObj.FieldUpdates || {};   // <-- the update payload

  if (!key) continue;

  if (!grouped[key]) {
    grouped[key] = {
      issueKey: key,
      fieldUpdates: []
    };
  }

  grouped[key].fieldUpdates.push(fu);
}

// return one n8n item per unique issue key
return Object.values(grouped).map(g => ({ json: g }));

